#
#* Look at the Image in adityaverma/images to check the logic of lcs

def lcs(s1, s2, n, m, memo, length):
    #base condition
    if n == 0 or m == 0:
        return length
    
    #memo
    key = (n-1, m-1, length)
    if key in memo.keys():
        return memo[key]

    # if present letters are same then length of subtring formed till now
    if s1[n-1] == s2[m-1]:
        length = lcs(s1, s2, n-1, m-1, memo, length+1) 

    # finally the max of present length and the max of the lengths obtained from both calls
    length = max(length, max(lcs(s1, s2, n-1, m, memo, 0), lcs(s1, s2, n, m-1, memo, 0)))
    memo[key] = length

    return length
    
